ui.UiFactory.layouts.settingsMenuLayout = {
    "type": "ui.FreeLayout",
    "frame": [0, 0, Graphics.width, Graphics.height],
    "preload": { 
        graphics: [name: $ -> $dataFields.database.system.menuBackground.name or 'bg-generic'],
        "sounds": [{ "name": $ -> $dataFields.database.system.menuSelectSound?.name }]
    },
    "controls": [
        {
            "executeFieldFormulas": true,
            "type": "ui.Image",
            "image": -> $dataFields.database.system.menuBackground.name or 'bg-generic',
            "frame": [0, 0, Graphics.width, Graphics.height]
        },
        {
            "type": "ui.StackLayout",
            "orientation": "vertical",
            "sizeToFit": true,
            "alignmentX": "center",
            "alignmentY": "center",
            "controls": [
                {
                    "type": "ui.Button",                    
                    "params": { "text": { "lcId": "149D0E4B8AF8574E5A8BF574FD9A9590B69A", "defaultText": "General" }, "action": { "name": "switchLayout", "params": { "name": "settingsGeneral", "savePrevious": true } } },
                    "frame": [0, 0, 300, 55],
                    "margin": [0, 0, 0, 20]
                },
                {
                    "type": "ui.Button",
                    "id": "message",
                    "params": { "text": { "lcId": "6683560332F9274AEE1B5D96FCA020F57DDE", "defaultText": "Message" }, "action": { "event": "onMouseClick", "name": "switchLayout", "params": { "name": "settingsMessage", "savePrevious": true } } },
                    "frame": [0, 0, 300, 55],
                    "margin": [0, 0, 0, 20]
                },
                {
                    "type": "ui.Button",
                    "params": { "text": { "lcId": "92FDB732611153400B595AD7D92206CDC592", "defaultText": "Audio" }, "action": { "event": "onMouseClick", "name": "switchLayout", "params": { "name": "settingsAudio", "savePrevious": true } } },
                    "frame": [0, 0, 300, 55],
                    "margin": [0, 0, 0, 20]
                },
                {
                    "type": "ui.Button",
                    "formulas": [$ -> o.visible = $tempFields.inGame]
                    "params": { 
                        "text": { "lcId": "A6EE5D6732DD454DDE988B569929C5F2DA62", "defaultText": "Back to Title" }, 
                        "action": { "name": "createControl", "params": { "descriptor": { 
                            "id": "confirmationDialog",
                            "type": "ui.ConfirmationDialog",
                            "zIndex": 90000,
                            "params": { 
                                "message": { "lcId": "ABC85CC3669B83481309BDF44B98EF11B59B", "defaultText": "Do you really want to exit?" },
                                "acceptActions": [{"name":"exitGame"},{"name": "switchLayout", "params": { "name": "titleLayout", "clear": true } }] , 
                                "rejectActions": [{"name":"disposeControl","params":($ -> 'confirmationDialog')}]}
                        } } }
                    },
                    "frame": [0, 0, 300, 55],
                    "margin": [0, 0, 0, 20]
                }
            ]
        },
        {
            "type": "ui.BackButton",
            "frame": [Graphics.width - 170, Graphics.height - 65, 150, 45]
        }
    ]
}

