ui.UiFactory.customTypes["ui.OptionButtonSet"] = {
    "type": "ui.StackLayout",
    "sizeToFit": true,
    "controls": [
        {
            "type": "ui.OptionButton",
            "params": { 
                "label": (-> p.offLabel), 
                "action": (-> p.offAction), 
                "read": ($ (-> !@data.exec()), (-> p.read)),
                "write": ($ ((v) -> @data.exec(!v)), (-> p.write)),
                "group": (-> p.group)
            },
            "margin": [0, 0, 0, 0]
        },
        {
            "type": "ui.OptionButton",
            "params": { 
                "label": (-> p.onLabel), 
                "action": (-> p.onAction), 
                "read": ($ (-> @data.exec()), (-> p.read)),
                "write": ($ ((v) -> @data.exec(v)), (-> p.write)),
                "group": (-> p.group)
            }
        }
    ]
}

