                    "controls":[
                        {
                            "type": "ui.SelectableWindow",
                            "frame": [0, 0, 750, 50],
                            "inheritProperties": true,
                            "params": {
                                "action": {"name": "emitEvent", "params": { "name": "selectionAccept", "source": ($ -> o.parent.parent.parent.parent.parent), "data": ($ -> $dataFields.scene.choices[o.parent.parent.index]) }}
                            },
                            "zIndex": 4999
                        },
                        {
                            "type": "ui.Text",
                            "inheritProperties": true,
                            "formatting": true,
                            "wordWrap": false,
                            "sizeToFit": true,
                            "styles": ["regularUIText"],
                            "alignmentX": 1,
                            "alignmentY": 1,
                            "frame": [0, 0],
                            "formulas": [$ -> o.text = lcs($dataFields.scene.choices[o.parent.index]?.text)],
                            "zIndex": 5100
                        }
                    ]
                }
            }
        }

    ]
}

