ui.UiFactory.customTypes["ui.InputTextBox"] = {
    "type": "ui.FreeLayout",
    "frame": [0, 0, Graphics.width, Graphics.height],
    "controls": [
        {
            "type": "ui.FreeLayout",
            "sizeToFit": true,
            "alignmentX": "center",
            "frame": [0, 10],
            "zIndex": 1000,
            "controls": [
                {
                    "type": "ui.FreeLayout",
                    "sizeToFit": true,
                    "controls": [
                        {
                            "type": "ui.Window",
                            "frame": [0, 0, "MAX($tempFields.letters * 110, 900)", 490],
                            "zIndex": 5
                        },
                        {
                            "type": "ui.Panel",
                            "style": "windowSubPanel",
                            "frame": ["MAX($tempFields.letters * 110, 900) - 130", 180, 130, 470 - 160],
                            "zIndex": 10
                        },
                        {
                            "type": "ui.Panel",
                            "color": [111, 111, 111],
                            "frame": [0, 180, "MAX($tempFields.letters * 110, 900)", 1],
                            "zIndex": 10
                        },
                        {
                            "type": "ui.Text",
                            "styles": ["regularUIText"],
                            "sizeToFit": true,
                            "text": "Text Entry",
                            "zIndex": 10,
                            "frame": [20, 15]
                        },
                        {
                            "type": "ui.StackLayout",
                            "components": [{ "id": "textInput", "type": "Component_TextInput", "params": { "letters": ($ -> $tempFields.letters) } }],
                            "sizeToFit": true,
                            "id": "textField",
                            "zIndex": 10,
                            "alignmentX": "center",
                            "frame": [12, 50],
                            "dataField": $ -> $tempFields.letters
                            "template": {
                                "type": "ui.FreeLayout",
                                "margin": [5, 5, 5, 5],
                                "sizeToFit": true,
                                "controls": [
                                    {
                                        "type": "ui.Image",
                                        "image": "entrybox"
                                    },
                                    {
                                        "type": "ui.Text",
                                        "frame": [0, 0],
                                        "sizeToFit": true,
                                        "alignmentX": "center",
                                        "alignmentY": "center",
                                        "color": [0, 0, 0, 0],
                                        "style": "textInputEntryText",
                                        "text": ""
                                    }
                                ]
                            }
                        }
                    ]
                },
                {
                    "type": "ui.GridLayout",
                    "sizeToFit": true,
                    "id": "charPage1_1",
                    "rows": 6,
                    "order": 10,
                    "columns": 5,
                    "alignmentY": 0,
                    "frame": [30, 185],
                    "cellSpacing": [0, 0, 20, 0],
                    "controls": $ -> $dataFields.textInputPages[0]
                },
                {
                    "type": "ui.GridLayout",
                    "id": "charPage1_2",
                    "sizeToFit": true,
                    "rows": 6,
                    "columns": 5,
                    "alignmentY": 0,
                    "frame": [420, 185],
                    "cellSpacing": [0, 0, 20, 0],
                    "controls": $ -> $dataFields.textInputPages[1]
                },
                {
                    "type": "ui.GridLayout",
                    "sizeToFit": true,
                    "id": "charPage2_1",
                    "visible": false,
                    "rows": 6,
                    "order": 10,
                    "columns": 5,
                    "alignmentY": 0,
                    "frame": [30, 185],
                    "cellSpacing": [0, 0, 20, 0],
                    "controls": $ -> $dataFields.textInputPages[2]
                },
                {
                    "type": "ui.GridLayout",
                    "id": "charPage2_2",
                    "visible": false,
                    "sizeToFit": true,
                    "rows": 6,
                    "columns": 5,
                    "alignmentY": 0,
                    "frame": [420, 185],
                    "cellSpacing": [0, 0, 20, 0],
                    "controls": $ -> $dataFields.textInputPages[3]
                },
                {
                    "type": "ui.TextBackspace", "params": { "target": ($ -> 'textField.textInput') },
                    "frame": ["100% - 120", 260]
                },
                {
                    "type": "ui.TextAccept",
                    "frame": ["100% - 120", 200]
                },
                {
                    "type": "ui.Text",
                    "sizeToFit": true,
                    "styles": ["regularUIText"],
                    "frame": ["100% - 120", 320],
                    "text": "Page",
                    "zIndex": 5000,
                    "action": {"name": "executeFormulas", "params": [
                        $ -> $charPage1_1.visible = !$charPage1_1.visible
                        $ -> $charPage1_2.visible = !$charPage1_2.visible
                        $ -> $charPage2_1.visible = !$charPage2_1.visible
                        $ -> $charPage2_2.visible = !$charPage2_2.visible
                    ] }
                }
            ]
        }
    ]
}

ui.UiFactory.customTypes["ui.Letter"] = {
    "type": "ui.FreeLayout",
    "sizeToFit": true,
    "controls": [
        {
            "type": "ui.Frame",
            "styles": ["selectableWindowShadow"],
            "zIndex": 5000,
            "padding": [-16, -16, -16, -16],
            "frame": [0, 0, 50, 50],
            "actions": [{"name": "action_addLetter", "target": (-> p.target), "params": { "letter": (-> p.text) } }]
        },
        {
            "type": "ui.Text",
            "sizeToFit": true,
            "alignmentX": "center",
            "alignmentY": "center"
            "style": "textInputLetterText",
            "text": -> p.text
            "zIndex": 5000
        }
    ]
}
ui.UiFactory.customTypes["ui.TextBackspace"] = {
    "type": "ui.FreeLayout",
    "sizeToFit": true,
    "controls": [
        {
            "type": "ui.Text",
            "sizeToFit": true,
            "styles": ["regularUIText"],
            "text": "Back",
            "zIndex": 5000,
            "action": {"name": "action_removeLetter", "target": (-> p.target), "params": { "letter": (-> p.text) } }
        }
    ]
}
ui.UiFactory.customTypes["ui.TextAccept"] = {
    "type": "ui.FreeLayout",
    "sizeToFit": true,
    "controls": [
        {
            "type": "ui.Text",
            "sizeToFit": true,
            "styles": ["regularUIText"],
            "text": "Confirm",
            "zIndex": 5000,
            "action": {"name": "emitEvent", "params": { "name": "accept", "source": ($ -> o.parent.parent.parent), "data": { "text": ($ -> $textField.textInput.text) } } }
        }
    ]
}

