            "sizeToFit": true,
            "alignmentX": "center",
            "frame": [0, 10],
            "zIndex": 1000,
            "controls": [
                {
                    "type": "ui.FreeLayout",
                    "sizeToFit": true,
                    "controls": [
                        {
                            "type": "ui.Window",
                            "frame": [0, 0, "MAX($tempFields.digits * 110, 460)", 470]
