                        {
                            "type": "ui.StackLayout",
                            "components": [{ "id": "numberInput", "type": "Component_NumberInput", "params": { "digits": ($ -> $tempFields.digits) } }],
                            "sizeToFit": true,
                            "id": "numberField",
                            "zIndex": 10,
                            "alignmentX": "center",
                            "frame": [12, 50],
                            "dataField": $ -> $tempFields.digits
