                        },
                        {
                            "type": "ui.Number", "params": { "text": "5", "number": 5, "target": ($ -> 'numberField.numberInput') }
                        },
                        {
                            "type": "ui.Number", "params": { "text": "6", "number": 6, "target": ($ -> 'numberField.numberInput') }
                        },
                        {
                            "type": "ui.Number", "params": { "text": "7", "number": 7, "target": ($ -> 'numberField.numberInput') }
                        },
                        {
                            "type": "ui.Number", "params": { "text": "8", "number": 8, "target": ($ -> 'numberField.numberInput') }
                        },
                        {
                            "type": "ui.Number", "params": { "text": "9", "number": 9, "target": ($ -> 'numberField.numberInput') }
                        },
                        {
                            "type": "ui.Number", "params": { "text": "0", "number": 0, "target": ($ -> 'numberField.numberInput') }
                        }
                    ]
                },
                {
                    "type": "ui.NumberBackspace", "params": { "target": ($ -> 'numberField.numberInput') },
                    "frame": ["100% - 120", 260]
                },
                {
                    "type": "ui.NumberAccept",
                    "frame": ["100% - 120", 200]
                }
            ]
        }
    ]
}

